library(graphics)
library(tcltk)

# Load the data generated by 01GenerateData.R
plot.data <- read.csv("GeneratedData/VoteDensityHighRes.csv", header=TRUE, stringsAsFactors=FALSE)

# Add padding above/below where there was data

# On top
top.padding <- 1:23
for (i in top.padding) {
  plot.data <- cbind(0, plot.data)
}
# On bottom
bottom.padding <- 1:23
for (i in bottom.padding) {
  plot.data <- cbind(plot.data, 0)
}

# On left
zero.row <- vector(mode="integer", length=dim(plot.data)[1])

left.padding <- 1:10
for (i in left.padding) {
  plot.data <- rbind(zero.row, plot.data)
}

# On right
right.padding <- 1:10
for (i in left.padding) {
  plot.data <- rbind(plot.data, zero.row)
}


max <- max(plot.data) # Max value in the data, used for scaling
plottingHeight <- 1000 # Arbitrary number that provides the graph's height
scaleFactor <- 500 # Discovered through trial and error to keep the graph in the boundaries
gap <- plottingHeight / length(plot.data) # Space between lines

# x, y: the x and y coordinates of the hull points
# n: the number of points in the curve.
bezierCurve <- function(x, y, n=10)
{
  outx <- NULL
  outy <- NULL
  
  i <- 1
  for (t in seq(0, 1, length.out=n))
  {
    b <- bez(x, y, t)
    outx[i] <- b$x
    outy[i] <- b$y
    
    i <- i+1
  }
  
  return (list(x=outx, y=outy))
}

bez <- function(x, y, t)
{
  outx <- 0
  outy <- 0
  n <- length(x)-1
  for (i in 0:n)
  {
    outx <- outx + choose(n, i)*((1-t)^(n-i))*t^i*x[i+1]
    outy <- outy + choose(n, i)*((1-t)^(n-i))*t^i*y[i+1]
  }
  
  return (list(x=outx, y=outy))
}

# Output the file to a 36 inch by 24 inch SVG canvas
plot.width = 36
plot.height = 24
svg(filename = "./TestPlots/CanadaNoThresh.svg", pointsize=12, width=plot.width, height=plot.height)

# Create a blank plot
yVals <- as.vector(plot.data[[1]] / max * scaleFactor)
plot(0, 0, xlim=c(0, length(yVals)), ylim=c(0,1100), type="n", las=1, xlab=NA, ylab=NA, bty="n", axes=FALSE)

plotting.threshold <- 0

plot.length = length(plot.data)
pb = tkProgressBar(title = "Plot Progress", label = "", min = 1, max = plot.length, initial = 1, width = 300)
for (i in 1:plot.length) {
  # Grabs a row of data
  yVals <- as.vector(plot.data[[i]] / max * scaleFactor)
  xVals <- c(0:(length(yVals) - 1))
  
  # Resolution in mm (I'm mixing metric and imperial here...blame me, I'm Canadian). 24 mm = 1 inch
  # resolution = 1
  # point.count = plot.width * 24 * resolution
  
  polygon(bezierCurve(xVals, yVals + plottingHeight, 30), border = NA, col = "#ffffff")
  lines(bezierCurve(xVals, yVals + plottingHeight, 30), col="#cccccc", lwd=1.5)
  
  # # Plot the peaks with a darker line. I have not updated this since switching the smoothing to bezier from spline so just uncommenting it won't work.
  # j <- 2 # Skip padding
  # while (j <= (length(yVals) - 2)) {
  #   
  #   if ((yVals[j] - plottingHeight) > plotting.threshold | (yVals[j+1] - plottingHeight) > plotting.threshold) {
  #     segments(xVals[j], yVals[j], xVals[j+1], yVals[j+1], col="#000000", lwd=1.5)
  #   } else { } # Do nothing
  #   
  #   j <- j + 1
  #   
  # }
  
  plottingHeight <- plottingHeight - gap
  
  # Update the progress bar
  info <- sprintf("%d%% Complete", round(i / plot.length))
  setTkProgressBar(pb, i)
}

